import React, { Component } from 'react';
import Searchbar from './components/Searchbar.jsx';
import fetchPics from './fetch';
import ImageGallery from './components/ImageGallery';
import Button from './components/Button';
import style from './style.module.css';

class App extends Component {
  state = {
    pics: [],
    error: null,
    loading: false,
    searchQuery: '',
    page: 0,
  };
  handleInput = (e) => {
    this.setState({ searchQuerry: e.tagerget.value });
  };
  onSubmit = () => {
    this.setState({ searchQuerry: query });
    fetchPics(this.state.searchQuery)
      .then((pics) =>
        this.setState((prevState) => ({ pics, page: prevState.page + 1 }))
      )
      .catch((error) => this.setState({ error }))
      .finally(() => this.setState({ loading: false }));
  };

  componentDidUpdate(prevProps, prevState) {
    const prevQuery = prevState.searchQuerry;
    const nextQuery = this.state.searchQuery;
    if (prevQuery !== nextQuery) {
      fetchPics(nextQuery);
    }
  }
  render() {
    const pics = this.state.pics;
    return (
      <div className={style.App}>
        <Searchbar handleInput={this.ha} onSubmit={this.onSubmit} />
        <ImageGallery pics={this.state.pics} />
        {pics.length > 0 && <Button />}
      </div>
    );
  }
}

export default App;
