import React, { Component } from 'react';
import Searchbar from './components/Searchbar.jsx';
import fetchPics from './fetch';
import ImageGallery from './components/ImageGallery';
import Button from './components/Button';
import style from './style.module.css';

class App extends Component {
  state = {
    pics: [],
    error: null,
    loading: false,
    searchQuery: '',
    page: 1,
  };

  doFetch = () => {
    const { searchQuery, page } = this.state;
    fetchPics(searchQuery, page)
      .then((pics) =>
        this.setState((prevState) => ({ pics:[prevStatepics], page: prevState.page + 1 }))
      )
      .catch((error) => this.setState({ error }))
      .finally(() => this.setState({ loading: false }));
  };
  handleQuerySaver = (query) => {
    this.setState({ searchQuery: query });
  };

  componentDidUpdate(prevProps, prevState) {
    const prevQuery = prevState.searchQuery;
    const nextQuery = this.state.searchQuery;
    if (prevQuery !== nextQuery) {
      this.doFetch();
    }
  }
  render() {
    const pics = this.state.pics;
    return (
      <div className={style.App}>
        <Searchbar onSubmit={this.handleQuerySaver} />
        <ImageGallery pics={this.state.pics} />
        {pics.length > 0 && <Button loadMore={this.doFetch} />}
      </div>
    );
  }
}

export default App;
